import axios from "axios";
import React from "react";
import "./ModerQuestion.css";

export default function ModerQuestion() {
  const [question, setQuestion] = React.useState(null);
  const [questionTitle, setQuestionTitle] = React.useEffect(null);

  React.useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/questions`).then((response) => {
      setQuestion(response.data);
    });
  }, []);

  function createQuestion(questionTitle) {
    axios
      .post(`${process.env.REACT_APP_API_URL}/questions`, {
        title: questionTitle,
      })
      .then((response) => {
        setQuestion(response.data);
      });
  }

  function deleteQuestion(questionId) {
    axios
      .delete(`${process.env.REACT_APP_API_URL}/questions/${questionId}`)
      .then(() => {
        setQuestion(question.filter((question) => question.id !== questionId));
      })
      .catch((error) => {
        console.log({ ...error });
      });
  }

  if (!question) return null;
  return (
    <table className="one">
      {question.map((question) => {
        return (
          <td>
            {question.title}
            <br />
            <button
              className="floating-button"
              onClick={() => {
                const { id } = question;
                deleteQuestion(id);
              }}
            >
              Delete question
            </button>
            <br />
            <br />
          </td>
        );
      })}

      <input
        placeholder="Вопрос"
        onChange={(event) => setQuestionTitle(event.target.value)}
      />

      <button
        className="floating-button"
        onClick={() => createQuestion(questionTitle)}
      >
        Create question
      </button>
    </table>
  );
}
